import React, { useState } from 'react';
import braceletRing from './images/ringbracelet.jpg';
import products from './products.json';
import './Bracelet.css';

const braceletFilters = ["All", "Pendant", "Pearl", "Statement", "Initial", "Emerald"];

const Bracelet = () => {
  const [selectedFilter, setSelectedFilter] = useState("All");

  const filteredProducts = products.filter(
    (product) =>
      product.category === "Bracelet" &&
      (selectedFilter === "All" || product.subcategory === selectedFilter)
  );

  return (
    <div>
      <div className="slideshow-container">
        <img src={braceletRing} alt="Slideshow" className="slideshow-image" />
      </div>

      {/* Filter Tabs */}
      <div className="filter-tab-container">
        {braceletFilters.map((filter, index) => (
          <button
            key={index}
            className={`filter-tab ${selectedFilter === filter ? 'active' : ''}`}
            onClick={() => setSelectedFilter(filter)}
          >
            {filter}
          </button>
        ))}
      </div>

      {/* Product Cards */}
      <div className="product-grid">
        {filteredProducts.map((product) => (
          <div key={product.id} className="product-card">
            <img
              src={require(`./images/${product.image}`)}
              alt={product.name}
              className="product-img"
            />
            <h3>{product.name}</h3>
            <p>₹{product.discountedPrice} <span className="strikethrough">₹{product.price}</span></p>
            <p>⭐ {product.rating}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Bracelet;
