import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import Navbar from './MyComponents/Navbar';
import Home from './MyComponents/Home';
import Footer from './MyComponents/Footer';
import Bracelet from './MyComponents/Bracelet';
import Ring from './MyComponents/Ring';
import Necklace from './MyComponents/Necklace';
import Mangalsutra from './MyComponents/Mangalsutra';
import Earring from './MyComponents/Earring';
import ProductPage from './MyComponents/Productpage';
import Login from './MyComponents/login';
import Signup from './MyComponents/signup';
import Like from './MyComponents/Like';
import products from './MyComponents/products.json'


function AppContent({ likedProducts, handleLike }) {
  const location = useLocation();
  const isAuthRoute = location.pathname === '/login' || location.pathname === '/signup';

  return (
    <>
      {!isAuthRoute && <Navbar />}
      <Routes>
      <Route path="/" element={<Home products={products} likedProducts={likedProducts} onLike={handleLike} />} />

        <Route path="/bracelet" element={<Bracelet />} />
        <Route path="/mangalsutra" element={<Mangalsutra />} />
        <Route path="/necklace" element={<Necklace />} />
        <Route path="/rings" element={<Ring />} />
        <Route path="/earrings" element={<Earring />} />
        <Route path="/product/:productId" element={<ProductPage />} />
        <Route path="/login" element={<Login />} />
        <Route path="/signup" element={<Signup />} />
        <Route path="/wishlist" element={<Like likedProducts={likedProducts} onLike={handleLike} />} />



      </Routes>
      {!isAuthRoute && <Footer />}
    </>
  );
}

function App() {
  const [likedProducts, setLikedProducts] = useState([]);

  useEffect(() => {
    const savedLikes = localStorage.getItem('likedProducts');
    if (savedLikes) {
      setLikedProducts(JSON.parse(savedLikes));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('likedProducts', JSON.stringify(likedProducts));
  }, [likedProducts]);

  const handleLike = (product) => {
    const alreadyLiked = likedProducts.some((p) => p.id === product.id);
    if (alreadyLiked) {
      setLikedProducts(likedProducts.filter((p) => p.id !== product.id));
    } else {
      setLikedProducts([...likedProducts, product]);
    }
  };

  return (
    <Router>
       <AppContent likedProducts={likedProducts} handleLike={handleLike} />
    </Router>
  );
}

export default App;
