import React from "react";
import "./Cart.css";

const Cart = ({ cartItems, onRemoveFromCart }) => {
  const calculateTotal = () =>
    cartItems.reduce((acc, item) => acc + item.discountedPrice, 0);

  return (
    <div className="cart-page">
      <h2>Your Cart ðŸ›’</h2>

      {cartItems.length === 0 ? (
        <p className="empty-msg">Your cart is empty.</p>
      ) : (
        <>
          <div className="cart-items">
            {cartItems.map((item) => (
              <div className="cart-item" key={item.id}>
                <img src={require(`./images/${item.image}`)} alt={item.name} className="cart-item-img" />
                <div className="cart-item-details">
                  <h4>{item.name}</h4>
                  <p>â‚¹{item.discountedPrice}</p>
                  <button className="remove-btn" onClick={() => onRemoveFromCart(item.id)}>Remove</button>
                </div>
              </div>
            ))}
          </div>

          <div className="cart-summary">
            <h3>Total: â‚¹{calculateTotal()}</h3>
            <button
              className="checkout-btn"
              onClick={() => {
                localStorage.setItem('checkoutMode', 'cart');
                localStorage.setItem('cartForCheckout', JSON.stringify(cartItems));
                window.location.href = '/checkout';
              }}
            >
              Proceed to Checkout
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default Cart;
