import React, { useState, useEffect } from 'react';
import './Profile.css';
import { useNavigate } from 'react-router-dom';
import { FaUser, FaBox, FaMapMarkerAlt, FaHeart, FaCog, FaSignOutAlt } from 'react-icons/fa';

const Profile = () => {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('user')) || {};
  const [name, setName] = useState(user.name || '');
  const [email, setEmail] = useState(user.email || '');
  const [phone, setPhone] = useState(user.phone || '');

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login');
  };

  const handleSave = () => {
    const updatedUser = { ...user, name, email, phone };
    localStorage.setItem('user', JSON.stringify(updatedUser));
    alert('Changes saved!');
  };

  return (
    <div className="account-wrapper">
      <h2 className="account-title">My Account</h2>
      <div className="account-container">
        <div className="account-sidebar">
          <div className="account-avatar">
            <div className="avatar-circle">{name?.charAt(0).toUpperCase()}</div>
            <div>
              <strong>{name}</strong>
              <p>{email}</p>
            </div>
          </div>
          <ul className="account-menu">
            <li className="active"><FaUser /> Profile</li>
            <li><FaBox /> My Orders</li>
            <li><FaMapMarkerAlt /> Addresses</li>
            <li><FaHeart /> Wishlist</li>
            <li><FaCog /> Settings</li>
            <li onClick={handleLogout} className="logout"><FaSignOutAlt /> Logout</li>
          </ul>
        </div>

        <div className="account-content">
          <h3>Profile Information</h3>
          <form className="profile-form" onSubmit={(e) => e.preventDefault()}>
            <label>Name</label>
            <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
            <label>Email</label>
            <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
            <label>Phone</label>
            <input type="text" value={phone} onChange={(e) => setPhone(e.target.value)} />
            <button type="button" onClick={handleSave}>Save Changes</button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Profile;
