import React, { useState,useEffect } from 'react';
import braceletRing from './images/mangalsutra.jpg';
import products from './products.json';
import ProductCard from './ProductCard'; // Import your custom card
import './Bracelet.css';
import { FaTruck, FaGem, FaUndoAlt } from 'react-icons/fa'

const braceletFilters = ["All", "Initial", "Emerald"];

const Bracelet = (onLike) => {
    useEffect(() => {
        window.scrollTo(0, 0);
      }, []);
  const [selectedFilter, setSelectedFilter] = useState("All");

  // Optional: Like feature handler (can connect to local state or backend)
  const [likedProducts, setLikedProducts] = useState(() => {
    const saved = localStorage.getItem('likedProducts');
    return saved ? JSON.parse(saved) : [];
  });

  const handleLike = (product) => {
    const isAlreadyLiked = likedProducts.some((p) => p.id === product.id);

    let updatedLikes;
    if (isAlreadyLiked) {
      // Remove from liked
      updatedLikes = likedProducts.filter((p) => p.id !== product.id);
    } else {
      // Add to liked
      updatedLikes = [...likedProducts, product];
    }

    setLikedProducts(updatedLikes);
    localStorage.setItem('likedProducts', JSON.stringify(updatedLikes));
  };


  // Filter bracelet items based on subcategory filter
  const filteredProducts = products.filter(
    (product) =>
      product.category === "Mangalsutra" &&
      (selectedFilter === "All" || product.cato === selectedFilter)
  );

  return (
    <div className="bracelet-page">
      {/* Hero Image */}
      <div className="slideshow-container">
        <img src={braceletRing} alt="Bracelet Collection" className="slideshow-image" />
      </div>
      <hr className="br" />
      <h3 className="title">Find Your Perfect Piece</h3>
      <hr className="br" />
      {/* Filter Tabs */}
      <div className="filter-tab-container">
        {braceletFilters.map((filter, index) => (
          <button
            key={index}
            className={`filter-tab ${selectedFilter === filter ? 'active' : ''}`}
            onClick={() => setSelectedFilter(filter)}
          >
            {filter}
          </button>
        ))}
      </div>

      {/* Product Grid */}
      <div className="product-grid">
        {filteredProducts.map((product) => (
          <ProductCard
          key={product.id}
          product={product}
          onLike={onLike}
          likedProducts={likedProducts}
        />
        ))}
      </div>

      <div className="feature-section">
      <div className="feature-item">
        <FaTruck className="feature-icon" />
        <h4>Free Shipping</h4>
        <p>On all orders above ₹999</p>
      </div>
      <div className="feature-item">
        <FaGem className="feature-icon" />
        <h4>Premium Quality</h4>
        <p>100% Authentic Jewelry</p>
      </div>
      <div className="feature-item">
        <FaUndoAlt className="feature-icon" />
        <h4>Easy Returns</h4>
        <p>7-day hassle-free return</p>
      </div>
    </div>
    </div>
  );
};

export default Bracelet;
