import React, { useState } from "react";
import { useParams } from "react-router-dom";
import productList from "./products.json";
import "./ProductPage.css";

const ProductPage = () => {
  const { productId } = useParams();
  const productData = productList.find((p) => p.id === Number(productId));

  if (!productData) return <div>Product not found.</div>;

  // Set the main image from `image` field
  const initialImage = productData.image || "";
  const [selectedImage, setSelectedImage] = useState(initialImage);

  return (
    <div className="product-page">
      {/* Gallery */}
      <div className="product-gallery">
        <div className="thumbnails">
          {productData.images.map((img, index) => (
            <img
              key={index}
              src={require(`./images/${img}`)}
              alt={`thumb-${index}`}
              onClick={() => setSelectedImage(img)}
              className={`thumbnail ${selectedImage === img ? "active" : ""}`}
            />
          ))}
        </div>

        <div className="main-image">
          <img
            src={require(`./images/${selectedImage}`)}
            alt="main"
          />
        </div>
      </div>

      {/* Info */}
      <div className="product-info">
        <h2>{productData.name}</h2>
        <p className="product-price">‚Çπ {productData.price}</p>
        <p className="product-description">{productData.description}</p>

        <ul className="product-details">
          {productData.details.map((detail, index) => (
            <li key={index}>{detail}</li>
          ))}
        </ul>

        <div className="buttons">
          <button className="btn-cart">Add to Cart</button>
          <button className="btn-wishlist">ü§ç</button>
          <button className="btn-buy">Buy it Now</button>
        </div>
      </div>
    </div>
  );
};

export default ProductPage;
