import React from "react";
import "./ProductCard.css"; // Ensure styles are updated

const ProductCard = ({ product }) => {
  const imagePath = require(`./images/${product.image}`); // Dynamic require
  return (
    <div className="product-card">
      <div className="image-container">
        <img src={imagePath} alt={product.name} className="product-image" />
        <div className="product-info-overlay">
          <ul>
            <li>18K GOLD PLATED</li>
            <li>1 Year Warranty</li>
            <li>Waterproof</li>
          </ul>
        </div>
      </div>
      <h3 className="product-name">{product.name}</h3>
      <p className="product-price">₹{product.discountedPrice}</p>
      <div className="product-rating">⭐ {product.rating}</div>
    </div>
  );
};

export default ProductCard;
