import React, { useState, useEffect } from 'react';
import './Home.css';
import first from './images/first.jpg';
import second from './images/second.jpg';
import ithird from './images/ithird.png'; 
import ifourth from './images/ifourth.png'; 
import ifirst from './images/ifirst.png';
import isecond from './images/isecond.png';
import ififth from './images/ififth.png';
import ProductCard from './ProductCard';  // Import the ProductCard component

const images = [first, second]; // Array of images for the slideshow
const imagesWithDetails = [
    { src: ifirst, name: "Bracelet", link: "/bracelet" },
    { src: isecond, name: "Mangalsutra", link: "/mangalsutra" },
    { src: ithird, name: "Necklace", link: "/necklace" },
    { src: ifourth, name: "Rings", link: "/rings" },
    { src: ififth, name: "Earrings", link: "/earrings" },
];

// Example products for "Office Wear" and "Date Night"
const officeWearProducts = [
  { name: "Gold Bracelet", actualPrice: "$100", discountedPrice: "$80", rating: 4.5, category: "Office Wear", inStock: true },
  { name: "Silver Necklace", actualPrice: "$150", discountedPrice: "$120", rating: 4.0, category: "Office Wear", inStock: true },
];

const dateNightProducts = [
  { name: "Diamond Earrings", actualPrice: "$500", discountedPrice: "$450", rating: 5.0, category: "Date Night", inStock: false },
  { name: "Platinum Ring", actualPrice: "$800", discountedPrice: "$700", rating: 4.7, category: "Date Night", inStock: true },
];

const Home = () => {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 8000); // Change the large image every 8 seconds

    return () => clearInterval(interval); // Cleanup interval when the component unmounts
  }, []);

  return (
    <div style={{ overflowX: 'scroll', whiteSpace: 'nowrap' }}>
      <img
        src={images[currentImageIndex]}
        alt="Slideshow"
        style={{ width: '1512px', height: '500px', display: 'inline-block' }}
      />

      <hr className="br" />
      <h3 className="title">Find Your Perfect Piece</h3>
      <hr className="br" />

      {/* Horizontal Scrollable Circle Images with Links */}
      <div className="circle-images">
        {imagesWithDetails.map((item, index) => (
          <div key={index} className="circle-image-container">
            <a href={item.link} target="_blank" rel="noopener noreferrer">
              <img src={item.src} alt={item.name} className="circle-image" />
            </a>
            <a
              href={item.link}
              target="_blank"
              rel="noopener noreferrer"
              className="circle-image-text"
            >
              {item.name}
            </a>
          </div>
        ))}
      </div>

      {/* Display Office Wear Products */}
      <h3 className="title">Office Wear</h3>
      <div className="product-list">
        {officeWearProducts.map((product, index) => (
          <ProductCard
            key={index}
            name={product.name}
            actualPrice={product.actualPrice}
            discountedPrice={product.discountedPrice}
            rating={product.rating}
            category={product.category}
            inStock={product.inStock}
          />
        ))}
      </div>

      {/* Display Date Night Products */}
      <h3 className="title">Date Night</h3>
      <div className="product-list">
        {dateNightProducts.map((product, index) => (
          <ProductCard
            key={index}
            name={product.name}
            actualPrice={product.actualPrice}
            discountedPrice={product.discountedPrice}
            rating={product.rating}
            category={product.category}
            inStock={product.inStock}
          />
        ))}
      </div>

      <hr className="br" />
    </div>
  );
};

export default Home;
