import React, { useState } from "react";
import ProductCard from "./ProductCard";
import data from "./.json"; // assuming your product JSON file is here

const Bracelet = () => {
  const [selectedFilter, setSelectedFilter] = useState("All");

  const braceletProducts = data.filter(
    (product) => product.category === "Bracelet"
  );

  const subcategories = [
    "All",
    ...new Set(braceletProducts.map((p) => p.subcategory)),
  ];

  const filtered = selectedFilter === "All"
    ? braceletProducts
    : braceletProducts.filter((p) => p.subcategory === selectedFilter);

  return (
    <div>
      <img src={require("./images/ringbracelet.jpg")} alt="Banner" style={{ width: "100%", height: "auto" }} />

      <div style={{ display: "flex", gap: "10px", padding: "20px", flexWrap: "wrap" }}>
        {subcategories.map((sub) => (
          <button
            key={sub}
            onClick={() => setSelectedFilter(sub)}
            style={{
              padding: "8px 16px",
              background: selectedFilter === sub ? "#333" : "#eee",
              color: selectedFilter === sub ? "#fff" : "#333",
              borderRadius: "20px",
              border: "none",
              cursor: "pointer"
            }}
          >
            {sub}
          </button>
        ))}
      </div>

      <div className="product-grid">
        {filtered.map((product) => (
          <ProductCard key={product.id} product={product} onLike={() => {}} />
        ))}
      </div>
    </div>
  );
};

export default Bracelet;
