import React, { useState } from "react";
import { FaHeart } from "react-icons/fa"; // Import the heart icon
import "./ProductCard.css";

const ProductCard = ({ product, onLike }) => {
  return (
    <div className="product-card">
      <img
        src={require(`./images/${product.image}`)} // Dynamic require for image
        alt={product.name}
        className="product-image"
      />
      <h3 className="product-name">{product.name}</h3>
      <p className="product-price">
        <span className="discounted-price">₹{product.discountedPrice}</span>{" "}
        <span className="original-price">₹{product.price}</span>
      </p>
      <p className="product-rating">⭐ {product.rating}</p>
      <p className="product-stock">{product.stock} in stock</p>
      <div className="like-button-container">
        <button
          className="like-button"
          onClick={() => Han(product)} // Pass the product to the onLike function
          aria-label="Wishlist"
        >
          <FaHeart className="icon-h" />
        </button>
      </div>
    </div>
  );
};

export default ProductCard;
