import React, { useState, useEffect } from 'react';
import './Home.css';
import first from './images/first.jpg';
import second from './images/second.jpg';
import { Link } from 'react-router-dom'; // If you're using React Router for navigation

const images = [
  { src: first, text: 'First Image' },
  { src: second, text: 'Second Image' }
];

const Home = () => {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 8000); 

    return () => clearInterval(interval); 
  }, []);

  return (
    <div style={{ overflowX: 'scroll', whiteSpace: 'nowrap' }}>
      <img
        src={images[currentImageIndex].src}
        alt="Slideshow"
        style={{ width: '1512px', height: '500px', display: 'inline-block' }}
      />

      <hr className="br" />
      <h3 className="title">Find Your Perfect Piece</h3>
      <hr className="br" />

      {/* Horizontal Scrollable Images List */}
      <div className="box">
        {images.map((image, index) => (
          <Link to={`/image/${index}`} key={index} className="image-link">
            <div className="image-container">
              <img
                src={image.src}
                alt={`Thumbnail ${index}`}
                className="scrollable-image"
              />
              <p className="image-text">{image.text}</p> {/* Text below the image */}
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default Home;
