import React, { useState, useEffect } from 'react';
import './Checkout.css';

const Checkout = ({ cart = [], onClearCart }) => {
  const [formData, setFormData] = useState({
    name: '',
    address: '',
    email: '',
    phone: '',
  });

  const [checkoutItems, setCheckoutItems] = useState([]);
  const [isBuyNow, setIsBuyNow] = useState(false);
  const [savedAddresses, setSavedAddresses] = useState([]);
const [selectedAddress, setSelectedAddress] = useState(null);


  useEffect(() => {
    const mode = localStorage.getItem('checkoutMode');
    const singleItem = JSON.parse(localStorage.getItem('checkoutItem'));

    if (mode === 'buyNow' && singleItem) {
      setCheckoutItems([singleItem]);
      setIsBuyNow(true);
    } else {
      setCheckoutItems(cart);
    }

    const fetchAddresses = async () => {
      try {
        const token = localStorage.getItem("token");
        const res = await fetch("http://localhost:4000/api/users/addresses", {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        const data = await res.json();
        setSavedAddresses(data);
      } catch (err) {
        console.error("Error fetching addresses:", err);
      }
    };

    fetchAddresses();

  }, [cart]);

  const handleChange = (e) => {
    setFormData({...formData, [e.target.name]: e.target.value});
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Use selected saved address if available, otherwise use form data
    const shippingInfo = selectedAddress ? {
      name: selectedAddress.name,
      address: selectedAddress.address,
      email: formData.email, // assume email is always typed manually
      phone: selectedAddress.phone || formData.phone, // fallback
    } : formData;

    const order = {
      items: checkoutItems,
      user: shippingInfo,
      total: totalPrice,
      date: new Date().toLocaleString(),
      mode: isBuyNow ? "Buy Now" : "Cart",
    };

    try {
      const token = localStorage.getItem("token");

      // Place Order
      const response = await fetch("http://localhost:4000/api/orders", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`
        },
        body: JSON.stringify(order)
      });

      if (!response.ok) throw new Error("Failed to place order");

      // âœ… Save new address if NOT using saved one
      if (!selectedAddress) {
        await fetch("http://localhost:4000/api/users/addresses", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`
          },
          body: JSON.stringify({
            name: formData.name,
            address: formData.address,
            phone: formData.phone,
            pincode: "000000" // or add a field in formData if needed
          })
        });
      }

      // ðŸ§¹ Cleanup localStorage and cart
      if (isBuyNow) {
        localStorage.removeItem('checkoutItem');
        localStorage.removeItem('checkoutMode');
      } else {
        localStorage.removeItem('checkoutItems');
        localStorage.removeItem('checkoutMode');
        onClearCart?.();
      }

      setFormData({ name: '', address: '', email: '', phone: '' });
      setCheckoutItems([]);

      alert('ðŸŽ‰ Order Placed Successfully!');
      window.location.href = '/';
    } catch (err) {
      console.error("Error submitting order:", err);
      alert("Failed to place order. Please try again.");
    }
  };




  const totalPrice = checkoutItems.reduce((sum, item) => sum + item.discountedPrice, 0);

  return (
    <div className="checkout-page">
      <h2>Checkout</h2>

      {checkoutItems.length === 0 ? (
        <p>No items to checkout.</p>
      ) : (
        <>
          <form className="checkout-form" onSubmit={handleSubmit}>
          <input
  name="name"
  type="text"
  placeholder="Full Name"
  value={formData.name}
  onChange={handleChange}
  required
  disabled={selectedAddress !== null}
/>

<input
  name="address"
  type="text"
  placeholder="Shipping Address"
  value={formData.address}
  onChange={handleChange}
  required
  disabled={selectedAddress !== null}
/>

<input
  name="email"
  type="email"
  placeholder="Email"
  value={formData.email}
  onChange={handleChange}
  required
/>

<input
  name="phone"
  type="tel"
  placeholder="Phone Number"
  value={formData.phone}
  onChange={handleChange}
  required
  disabled={selectedAddress !== null}
/>

            <div className="checkout-summary">
              <h4>Total Items: {checkoutItems.length}</h4>
              <h4>Total Price: â‚¹{totalPrice}</h4>
            </div>

            <button type="submit" className="place-order-btn">Place Order</button>
          </form>
          <div className="address-selection">
  <h3>Select a shipping address</h3>
  {savedAddresses.length === 0 && <p>No saved addresses, please fill below to add one.</p>}
  <ul>
    {savedAddresses.map((addr) => (
      <li key={addr._id}>
        <input
          type="radio"
          name="selectedAddress"
          value={addr._id}
          onChange={() => setSelectedAddress(addr)}
        />
        {addr.name} - {addr.address}, {addr.pincode}
      </li>
    ))}
  </ul>
</div>


          <div className="checkout-items-preview">
            {checkoutItems.map((item) => (
              <div key={item.id} className="checkout-item">
                <img src={`/images/${item.image}`} alt={item.name} />

                <div>
                  <p>{item.name}</p>
                  <p>â‚¹{item.discountedPrice}</p>
                </div>
              </div>
            ))}
          </div>
        </>
      )}
    </div>
  );
};

export default Checkout;
