import React, { useState } from "react";
import { Link } from "react-router-dom";
import "./Auth.css";
import { useNavigate } from 'react-router-dom';


const Signup = () => {
  const [step, setStep] = useState(1);

  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [otp, setOtp] = useState("");
  const navigate = useNavigate();

  const handleSendOtp = async (e) => {
    e.preventDefault();

    if (password !== confirmPassword) {
      alert("Passwords do not match");
      return;
    }

    try {
      const res = await fetch("http://localhost:4000/api/auth/send-otp", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email }),
      });
      const data = await res.json();
      if (res.ok) {
        alert(data.message);
        navigate("/login"); 
        // show OTP box
      } else {
        alert(data.error || "Could not send OTP");
      }
    } catch (err) {
      alert("Something went wrong while sending OTP");
    }
  };

  const handleVerifyAndSignup = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch("http://localhost:4000/api/auth/verify-otp", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name, email, phone, password, otp }),
      });
      const data = await res.json();
      if (res.ok) {
        alert(data.message);
        // optionally navigate to login page
      } else {
        alert(data.error || "Signup failed");
      }
    } catch (err) {
      alert("Something went wrong while verifying OTP");
    }
  };

  return (
    <div className="auth-wrapper">
      <div className="auth-image" />
      <div className="auth-container">
        <div className="auth-box">
          <h2 className="auth-title">Create Account</h2>

          {step === 1 && (
            <form onSubmit={handleSendOtp}>
              <input
                type="text"
                placeholder="Full Name"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
              />
              <input
                type="email"
                placeholder="Email Address"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
              <input
                type="text"
                placeholder="Phone Number"
                value={phone}
                onChange={(e) => setPhone(e.target.value)}
                required
              />
              <input
                type="password"
                placeholder="Create Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
              <input
                type="password"
                placeholder="Confirm Password"
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
                required
              />
              <button type="submit" className="auth-btn">Send OTP</button>
            </form>
          )}

          {step === 2 && (
            <form onSubmit={handleVerifyAndSignup}>
              <input
                type="text"
                placeholder="Enter OTP"
                value={otp}
                onChange={(e) => setOtp(e.target.value)}
                required
              />
              <button type="submit" className="auth-btn">Verify & Sign Up</button>
            </form>
          )}

          <div className="auth-switch">
            <p>
              Already have an account? <Link to="/login">Login</Link>
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Signup;
