import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import Navbar from './MyComponents/Navbar';
import Home from './MyComponents/Home';
import Footer from './MyComponents/Footer';
import Bracelet from './MyComponents/Bracelet';
import Ring from './MyComponents/Ring';
import Necklace from './MyComponents/Necklace';
import Mangalsutra from './MyComponents/Mangalsutra';
import Earring from './MyComponents/Earring';
import ProductPage from './MyComponents/Productpage';
import Login from './MyComponents/login';
import Signup from './MyComponents/signup';
import Like from './MyComponents/Like';
import productList from './MyComponents/products.json'

function AppContent({ likedProducts, handleLike }) {
  const location = useLocation();
  const isAuthRoute = location.pathname === '/login' || location.pathname === '/signup';

  return (
    <>
      {!isAuthRoute && <Navbar />}
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/bracelet" element={<Bracelet onLike={handleLike} likedProducts={likedProducts} />} />
        <Route path="/mangalsutra" element={<Mangalsutra onLike={handleLike} likedProducts={likedProducts} />} />
        <Route path="/necklace" element={<Necklace onLike={handleLike} likedProducts={likedProducts} />} />
        <Route path="/rings" element={<Ring onLike={handleLike} likedProducts={likedProducts} />} />
        <Route path="/earrings" element={<Earring onLike={handleLike} likedProducts={likedProducts} />} />
        <Route path="/product/:productId" element={<ProductPage onLike={handleLike} likedProducts={likedProducts} />} />
        <Route path="/login" element={<Login />} />
        <Route path="/signup" element={<Signup />} />
        <Route path="/likes" element={<Like likedProducts={likedProducts} />} />
      </Routes>
      {!isAuthRoute && <Footer />}
    </>
  );
}

function App() {
  const [likedProducts, setLikedProducts] = useState([]);

  const handleLike = (product) => {
    const isAlreadyLiked = likedProducts.some((p) => p.id === product.id);
    if (isAlreadyLiked) {
      setLikedProducts(likedProducts.filter((p) => p.id !== product.id));
    } else {
      setLikedProducts([...likedProducts, product]);
    }
  };

  return (
    <Router>
      <AppContent likedProducts={likedProducts} handleLike={handleLike} />
    </Router>
  );
}

export default App;
