import React, { useState } from "react";
import { useParams } from "react-router-dom";
import productList from "./products.json";

const ProductPage = () => {
  const { productId } = useParams();
  const productData = productList.find((p) => p.id === Number(productId));

  // Set the main image from `image` field
  const initialImage = productData?.image || "";
  const [selectedImage, setSelectedImage] = useState(initialImage);

  if (!productData) return <div>Product not found.</div>;

  return (
    <div className="product-container">
      {/* Main section */}
      <div className="product-top">
        <div className="product-gallery">
          <div className="thumbnails">
            {productData.images.map((img, index) => (
              <img
                key={index}
                src={require(`./images/${img}`)}
                alt={`thumb-${index}`}
                onClick={() => setSelectedImage(img)}
                className={`thumbnail ${selectedImage === img ? "active" : ""}`}
              />
            ))}
          </div>
          <div className="main-image">
            <img src={require(`./images/${selectedImage}`)} alt="main" />
          </div>
        </div>

        <div className="product-info">
          <h2 className="product-title">{productData.name}</h2>
          <p className="product-price">₹ {productData.price} <span className="tax-info">(Incl. of All Taxes)</span></p>
          <p className="product-description">{productData.description}</p>
          <ul className="product-details">
            {productData.details.map((detail, index) => (
              <li key={index}>{detail}</li>
            ))}
          </ul>
          <div className="buttons">
            <button className="btn-cart">Add to Cart</button>
            <button className="btn-wishlist">🤍</button>
          </div>
          <button className="btn-buy">Buy it Now</button>
        </div>
      </div>

      {/* Policy Section */}
      <div className="policy-section">
        <div className="policy-box">🚚 Express Shipping</div>
        <div className="policy-box">🔁 Easy Return & Exchange</div>
        <div className="policy-box">🔒 Secure Payments</div>
      </div>

      {/* Related/Recently Viewed */}
      <div className="related-section">
        <h3>Related products</h3>
        {/* You can map productList or relatedList here */}
        <div className="related-products">
          {/* Example Static Card */}
          <div className="related-card">
            <img src={require(`./images/sample1.jpg`)} alt="related" />
            <p className="related-title">Moksha Diamond Mangalsutra</p>
            <p className="related-price">1899.99</p>
          </div>
          {/* Add more cards */}
        </div>
      </div>
    </div>
  );
};

export default ProductPage;
