import React, { useState } from "react";
import ProductCard from "./ProductCard";
import "./like.css"; // Make sure you have appropriate styles here

const ProductGrid = ({ products }) => {
  const [likedProducts, setLikedProducts] = useState([]);

  const handleLike = (product) => {
    const alreadyLiked = likedProducts.find((p) => p.id === product.id);
    if (!alreadyLiked) {
      setLikedProducts([...likedProducts, product]);
    }
  };

  return (
    <div className="grid-container">
      <div className="product-grid">
        {products.map((product) => (
          <ProductCard key={product.id} product={product} onLike={handleLike} />
        ))}
      </div>

      {likedProducts.length > 0 && (
        <div className="liked-products">
          <h2>❤️ Liked Products</h2>
          <div className="liked-product-list">
            {likedProducts.map((product) => (
              <div key={product.id} className="liked-product-item">
                <img
                  src={require(`./images/${product.image}`)}
                  alt={product.name}
                  className="liked-product-image"
                />
                <p>{product.name}</p>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default ProductGrid;
